mapper.select:
select tb_vaccination.*,tb_vacpoint.vac_pointname from tb_vacpoint,tb_vaccination
where tb_vacpoint.vac_pointid=tb_vaccination.vac_pointid and tb_vaccination.user_id=#{user_id}
order by tb_vaccination.vac_time;

select tb_vaccination.*,tb_user.phone, tb_user.user_name,tb_user.user_identify,tb_vacpoint.vac_pointname
       from tb_vaccination,tb_user,tb_vacpoint where tb_vaccination.user_id in (select user_id from tb_user where user_name=#{user_name})
                                     and tb_vaccination.user_id=tb_user.user_id
                               and tb_vaccination.vac_pointid in (select distinct vac_pointid from tb_vacpoint where vac_pointname=#{vac_pointname})
                                                 and tb_vaccination.vac_pointid=tb_vacpoint.vac_pointid
                               and vac_time=#{vac_time} and dose=#{dose}
